			<em class="moderation"><?php _e('Your comment is awaiting moderation.', 'engine'); ?></em>
			<?php endif; ?>

			<!--BEGIN .comment-body-->
			<div class="comment-body">
				<?php comment_text() ?>
			<!--END .comment-body-->
			</div>

		<!--END #comment-id-->
		</div>

<?php
}


